<template>
	<!-- #ifdef APP -->
	<view class="face-avatar">
		<image v-if="resource && islocal" class="face-image" :src="resource" :mode="mode" :lazy-load="lazyLoad"
			:fade-show="fadeShow" :webp="webp" :show-menu-by-longpress="showMenuByLongpress" :style="[style]"
			@tap="fnEvent('click', $event)" @error="fnEvent('error', $event)" />
		</image>
		<image class="face-image" v-else :src="src" :mode="mode" @error="fnEvent('error', $event)"
			@load="fnEvent('load', $event)" />
	</view>
	<!-- #endif -->
	<!-- #ifdef MP -->
	<view class="face-avatar">
	<image class="face-image" v-if="imId" :src="src" :mode="mode" @error="($event)=>fnEvent('error', $event)"
		@load="($event)=>fnEvent('load', $event)" />
	</view>
	<!-- #endif -->
</template>
<script>
	//默认头像 
	// #ifndef MP
	let base64Avatar = 'data:image/jpeg;base64,'
	// #endif
	// #ifdef MP
	let base64Avatar = 'https://www.51mihuo.com/static/icon/app-logo.png'
	// #endif
	// #ifdef APP-PLUS
	import path from 'path'
	import imConfig from "@/config/baseUrl.js"
	import {
		resolveFile,
		funimgcache as storage
	} from './index'
	// #endif

	/**
	 * ImgCache 图片缓存
	 * @description APP端图片缓存
	 * @tutorial https://ext.dcloud.net.cn/plugin?id=2515
	 * @property {string} src 图片资源地址
	 * @property {string} mode 图片裁剪、缩放的模式
	 * @property {boolean} lazyLoad 是否图片懒加载
	 * @property {boolean} fade-show 图片显示动画效果
	 * @property {boolean} webp 默认不解析 webP 格式，只支持网络资源
	 * @property {boolean} show-menu-by-longpress 开启长按图片显示识别小程序码菜单
	 * @property {string} dir 缓存的文件目录，文件夹开头不能有_
	 * @property {string} width 宽度，单位任意，如果为数值，则为 rpx 单位
	 * @property {string} height 高度，单位任意，如果为数值，则为 rpx 单位
	 * @property {object} custom-style 自定义样式
	 * @event {Function} click 点击图片
	 * @event {Function} error 错误发生
	 * @event {Function} load 图片载入完毕
	 * @example <image-cache src="https://example.com/image.png"></mh-img-cache>
	 */
	let queue = [];
	export default {
		props: {
			lazy: {
				type: Boolean,
				default: true
			},
			imId: {
				type: Number,
				default: 0
			},
			mode: {
				type: String,
				default: 'aspectFit'
			},
			lazyLoad: {
				type: Boolean,
				default: false
			},
			fadeShow: {
				type: Boolean,
				default: true
			},
			webp: {
				type: Boolean,
				default: false
			},
			showMenuByLongpress: {
				type: Boolean,
				default: false
			},
			dir: {
				type: String,
				default: 'funimgcache'
			},
			width: {
				type: [String, Number]
			},
			height: {
				type: [String, Number]
			},
			customStyle: {
				type: Object,
				default: () => ({})
			}
		},
		data() {
			return {
				resource: '',
				islocal: false,
				src: ''
			}
		},
		computed: {
			style() {
				const style = {
					willChange: 'transform'
				}

				// 判断传过来的值是否为 undefined null false ''
				const isEmpty = val => [undefined, null, false, ''].includes(val);
				!isEmpty(this.width) && (style.width = this.addUnit(this.width));
				!isEmpty(this.height) && (style.height = this.addUnit(this.height));
				return {
					...style,
					...this.customStyle
				}
			}
		},
		watch: {

		},
		mounted() {
			this.init();
		},
		methods: {
			// 初始化
			init() {
				// #ifdef APP
				this.src = this.$util.avatarImImage(this.imId)
				this.fnCache()
				// #endif
				// #ifdef MP
				this.src = this.$util.avatarImImage(this.imId)
				// #endif
			},
			// 获取缓存
			async fnCache() {
				const url = this.$util.avatarImImage(this.imId)
				if (!/^https?:\/\//.test(url)) return this.setSrc(); // 判断是否网络地址
				const [select] = await storage.select({url}); // 查询缓存是否存在
				if (select) {
					const path = select.local == "error" ? base64Avatar : select.local;
					this.islocal = true;
					if (await resolveFile(path)) {
						this.setSrc(path)
						let currenttime = new Date() / 1000;
						if ((currenttime - select.timestamp) > 3 * 24 * 60 * 60) {
							setTimeout(() =>
								uni.removeSavedFile({
									filePath: path
								}), 2000);
							storage.delete(select)
						}
						return;
					};
					if (select.local != "error") {
						storage.delete(select); // 如果本地文件不存在则删除缓存数据
					}
				}
				this.setSrc();
			},
			// 设置图片资源地址
			setSrc(src) {
				this.resource = src || base64Avatar;
			},
			// 添加单位，如果为数值则为rpx单位，否则直接返回
			addUnit(value) {
				value = String(value)
				return /^(?:-?\d+|-?\d{1,3}(?:,\d{3})+)?(?:\.\d+)?$/.test(value) ? `${value}rpx` : value
			},
			// 发送事件
			async fnEvent(emit, event) {
				// #ifdef MP
				if (emit == "error") {
					this.src = 'https://www.51mihuo.com/static/icon/app-logo.png';
				}
				// #endif
				// #ifdef APP-PLUS
				if (emit == "error") {
					let timestamp = Math.floor(new Date().getTime() / 1000);
					storage.insert({
						url: this.$util.avatarImImage(this.imId),
						local: "error",
						timestamp
					})
					this.src = base64Avatar;
					return;
				}
				//这里应该是会先执行load再执行 error 但是经过测试这个也不一定是对的的顺序 
				//所以这里加判断 应为是头像的问题那么这里执行一个判断 判断下载的是否有后缀
				const local = await this.download(this.$util.avatarImImage(this.imId), this.dir); // 下载文件
				let timestamp = Math.floor(new Date().getTime() / 1000);
				if (local && local.indexOf('.') > 0) {
					storage.insert({
						url: this.$util.avatarImImage(this.imId),
						local,
						timestamp
					})
					return
				} else {
					//清除下载的空白文件
					
					uni.removeSavedFile({
						filePath: local,
						complete: function(res) {
							//console.log(res);
						}
					});
					
					storage.insert({
						url: this.$util.avatarImImage(this.imId),
						local: "error",
						timestamp
					})
					
					this.src = base64Avatar;
				};
				// #endif

				// 缓存数据
			},
			//这里的数据是暂时存为缓存 不是im的聊天记录。
			download(url, dir) {
				return new Promise(resolve => {
					if (queue.includes(url)) return
					queue.push(url)
					uni.downloadFile({
						url: url,
						success: (res) => {
							uni.saveFile({
								tempFilePath: res.tempFilePath,
								success: (data) => {
									let savedFilePath = data.savedFilePath;
									queue.splice(queue.indexOf(url), 1)
									resolve(savedFilePath || null);
								}
							});
						}
					})
				})
			},
			//这里我们创建im的聊天文件的缓存
			filename(url, dir) {
				const CHARS = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ'
				let random = ''
				for (let i = 0; i < 4; i++) {
					const index = parseInt(Math.random() * CHARS.length)
					random += CHARS[index]
				}

				dir = dir.replace(/(^\/)|(\/$)/g, '')
				const name = Date.now() + random + path.extname(url)
				return `_doc/${dir}/${name}`
			}
		}
	}
</script>
<style lang="scss">
	.face-image {
		width: 84rpx;
		height: 84rpx;
		border-radius: 50%;
	}
</style>
