<template>
	<view class="u-avatar" :style="[wrapStyle]" @tap="click">
		<image
			@error="loadError"
			:style="[imgStyle]"
			class="u-avatar__img"
			v-if="!uText && avatar"
			:src="avatar" 
			:mode="imgMode"
		></image>
		<text class="u-line-1" v-else-if="uText" :style="{
			fontSize: '38rpx'
		}">{{uText}}</text>
		<slot v-else></slot>
		<view class="u-avatar__sex" v-if="showSex" :class="['u-avatar__sex--' + sexIcon]" :style="[uSexStyle]">
			<u-icon :name="sexIcon" size="20"></u-icon>
		</view>
		<view class="u-avatar__level" v-if="showLevel" :style="[uLevelStyle]">
			<u-icon :name="levelIcon" size="20"></u-icon>
		</view>
	</view>
</template>

<script>
	let base64Avatar = 'data:image/jpeg;base64,'
	/**
	 * avatar 
	 * @description 
	 * @tutorial https://www.uviewui.com/components/avatar.html
	 * @property {String} bg-color #ffffff
	 * @property {String} src 
	 * @property {String Number} size (large, default, mini)rpxdefault
	 * @property {String} mode circle
	 * @property {String} sex-icon man-woman-man
	 * @property {String} level-icon level
	 * @property {String} sex-bg-color 
	 * @property {String} level-bg-color 
	 * @property {String} show-sex false
	 * @property {String} show-level false
	 * @property {String} img-mode uniimagemodewidthFixaspectFill
	 * @property {String} index v-forindex
	 * @event {Function} click 
	 * @example <u-avatar :src="src"></u-avatar>
	 */
	export default {
		name: 'u-avatar',
		props: {
			// 
			bgColor: {
				type: String,
				default: 'transparent'
			},
			// 
			src: {
				type: String,
				default: ''
			},
			// large-default-mini-rpx
			// 
			size: {
				type: [String, Number],
				default: 'default'
			},
			// square-circle-
			mode: {
				type: String,
				default: 'circle'
			},
			// 
			text: {
				type: String,
				default: ''
			},
			// 
			imgMode: {
				type: String,
				default: 'aspectFill'
			},
			// 
			index: {
				type: [String, Number],
				default: ''
			},
			// man-woman-
			sexIcon: {
				type: String,
				default: 'man'
			},
			// 
			levelIcon: {
				type: String,
				default: 'level'
			},
			// 
			levelBgColor: {
				type: String,
				default: ''
			},
			// 
			sexBgColor: {
				type: String,
				default: ''
			},
			// 
			showSex: {
				type: Boolean,
				default: false
			},
			// 
			showLevel: {
				type: Boolean,
				default: false
			}
		},
		data() {
			return {
				error: false,
				// props
                avatar: this.src ? this.src : base64Avatar, 
			}
		},
        watch: {
            src(n) {
                // 
                if(!n) {
					// null''undefined
					this.avatar = base64Avatar;
					this.error = true;
				} else {
					this.avatar = n;
					this.error = false;
				}
            }
        },
		computed: {
			wrapStyle() {
				let style = {};  
				style.height = this.size == 'large' ? '120rpx' : this.size == 'default' ?
				'90rpx' : this.size == 'mini' ? '70rpx' : this.size + 'rpx';
				style.width = style.height;
				style.flex = `0 0 ${style.height}`;
				style.backgroundColor = this.bgColor;
				style.borderRadius = this.mode == 'circle' ? '500px' : '5px';
				if(this.text) style.padding = `0 6rpx`;
				return style;
			},
			imgStyle() {
				let style = {};
				style.borderRadius = this.mode == 'circle' ? '500px' : '5px';
				return style;
			},
			// 
			uText() {
				return String(this.text)[0];
			},
			// 
			uSexStyle() {
				let style = {};
				if(this.sexBgColor) style.backgroundColor = this.sexBgColor;
				return style;
			},
			// 
			uLevelStyle() {
				let style = {};
				if(this.levelBgColor) style.backgroundColor = this.levelBgColor;
				return style;
			}
		},
		methods: {
			// 
			loadError() {
				this.error = true;
                this.avatar = base64Avatar;
				this.$emit("loadError",this.avatar);
			},
			click() {
				this.$emit('click', this.index);
			}
		}
	}
</script>

<style lang="scss" scoped>
	@import "../../libs/css/style.components.scss";

	.u-avatar {
		/* #ifndef APP-NVUE */
		display: inline-flex;		
		/* #endif */
		align-items: center;
		justify-content: center;
		font-size: 28rpx;
		color: $u-content-color;
		border-radius: 10px;
		position: relative;
		
		&__img {
			width: 100%;
			height: 100%;
		}
		
		&__sex {
			position: absolute;
			width: 32rpx;
			color: #ffffff;
			height: 32rpx;
			@include vue-flex;
			justify-content: center;
			align-items: center;
			border-radius: 100rpx;
			top: 5%;
			z-index: 1;
			right: -7%;
			border: 1px #ffffff solid;
			
			&--man {
				background-color: $u-type-primary;
			}
			
			&--woman {
				background-color: $u-type-error;
			}
			
			&--none {
				background-color: $u-type-warning;
			}
		}
		
		&__level {
			position: absolute;
			width: 32rpx;
			color: #ffffff;
			height: 32rpx;
			@include vue-flex;
			justify-content: center;
			align-items: center;
			border-radius: 100rpx;
			bottom: 5%; 
			z-index: 1;
			right: -7%;
			border: 1px #ffffff solid;
			background-color: $u-type-warning;
		}
	}
</style>
